const s = require('storyeng')
const should = require('should')

const text = `

a bunch of {sample} formatting with {foo}

and {more} of the {deep.stuff}
`

const formatted = `

a bunch of that formatting with bar

and what of the here
`

describe('format', () => {

  it( 'should have python-like formatting', done => {
    let data = {
      sample: 'that',
      foo: 'bar',
      more: 'what',
      deep: {stuff:'here'}
    }
    let blah = text.format(data)
    should(blah === formatted)
    done()
  })

})